import React, { useContext, useEffect, useRef, useState } from 'react';
import './Create.css';
import useFetch from '../../hooks/useFetch';
import { useNavigate, useParams } from 'react-router-dom'; // useParams import edilmeli
import { ThemeContext } from '../../contexts/ThemeContext';

function Edit() {
  const { id } = useParams(); // URL'deki id'yi al
  const url1 = `http://localhost:3000/tarifler/${id}`;
  const { data: tarif, isLoading, error } = useFetch(url1);

  const [baslik, setBaslik] = useState('');
  const [aciklama, setAciklama] = useState('');
  const [hazirlanisi, setHazirlanisi] = useState('');
  const [resim, setResim] = useState('');
  const [malzeme, setMalzeme] = useState('');
  const [malzemeler, setMalzemeler] = useState([]);
  const [url, setUrl] = useState('');
  const malzemeInput = useRef(null);
  const navigate = useNavigate();

  const { color, mode } = useContext(ThemeContext);
  const { postData, data } = useFetch(`http://localhost:3000/tarifler/${id}`, 'PUT'); // PUT metodu ile güncelleme yapılacak

  // Gelen tarif verilerini form state'lerine yerleştir
  useEffect(() => {
    if (tarif) {
      setBaslik(tarif.baslik || '');
      setAciklama(tarif.aciklama || '');
      setHazirlanisi(tarif.hazirlanisi || '');
      setResim(tarif.resim || '');
      setMalzemeler(tarif.malzemeler || []);
      setUrl(tarif.url || '');
    }
  }, [tarif]);

  const handleAddMalzeme = (e) => {
    const item = malzeme.trim();
    if (item && !malzemeler.includes(item)) {
      setMalzemeler((prevItems) => [...prevItems, item]);
    }
    setMalzeme('');
    malzemeInput.current.focus();
    malzemeInput.current.value = '';
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    postData({ baslik, aciklama, malzemeler, hazirlanisi, resim, url });
  };

  useEffect(() => {
    if (data) {
      navigate('/');
    }
  }, [data, navigate]);

  return (
    <div
      className={`text-${mode === 'dark' ? 'light' : 'dark'} border-${
        mode === 'dark' ? 'light' : 'dark'
      }`}
    >
      <h2>Tarif Düzenle</h2>
      {isLoading && <p>Yükleniyor...</p>}
      {error && <p>Hata: {error}</p>}
      {tarif && (
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="baslik" className="form-label">
              Başlık: {baslik}
            </label>
            <input
              type="text"
              name="baslik"
              id="baslik"
              className="form-control"
              value={baslik}
              onChange={(e) => setBaslik(e.target.value)}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="aciklama" className="form-label">
              Açıklama
            </label>
            <input
              type="text"
              name="aciklama"
              id="aciklama"
              className="form-control"
              value={aciklama}
              onChange={(e) => setAciklama(e.target.value)}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="malzemeler">
              Malzemeler{' '}
              <ul>
                {malzemeler.map((item) => (
                  <li key={item}>{item}</li>
                ))}
              </ul>
            </label>
            <div className="input-group">
              <input
                type="text"
                ref={malzemeInput}
                name="malzeme"
                className="form-control"
                onChange={(e) => setMalzeme(e.target.value)}
              />
              <button
                className={`btn btn-${color}`}
                type="button"
                onClick={handleAddMalzeme}
              >
                +
              </button>
            </div>
          </div>
          <div className="mb-3">
            <label htmlFor="hazirlanisi" className="form-label">
              Hazırlanışı
            </label>
            <textarea
              name="hazirlanisi"
              id="hazirlanisi"
              className="form-control"
              value={hazirlanisi}
              onChange={(e) => setHazirlanisi(e.target.value)}
            ></textarea>
          </div>
          <div className="mb-3">
            <label htmlFor="resim" className="form-label">
              Resim
            </label>
            <input
              type="text"
              name="resim"
              id="resim"
              className="form-control"
              value={resim}
              onChange={(e) => setResim(e.target.value)}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="url" className="form-label">
              Url
            </label>
            <input
              type="text"
              name="url"
              id="url"
              className="form-control"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
            />
          </div>
          <button type="submit" className={`btn btn-${color}`}>
            Kaydet
          </button>
        </form>
      )}
    </div>
  );
}

export default Edit;
