import React from 'react'
import './Home.css';
import ProductCard from '../../components/ProductCard';
import useFetch from '../../hooks/useFetch';

function Home() {

  const { data, isLoading, error } = useFetch("https://fastapi-production-azattekce.up.railway.app/tarifler")

  return (
    <div className="row mt-3">

      { isLoading && <div className='alert alert-warning'>Yükleniyor...</div>}
      { error && <div className='alert alert-danger'>{ error }</div>}


      {    
        data &&  data.tarifler.map((tarif, index) => (
          <ProductCard key={index} tarif={tarif} />
        ))
      }
    </div>
  )
}

export default Home
